<span class="line">    <span class="token function">translate</span><span class="token punctuation">(</span>0<span class="token punctuation">,</span> <span class="token function">calc</span><span class="token punctuation">(</span><span class="token function">var</span><span class="token punctuation">(</span>--icon-translation-size<span class="token punctuation">)</span> * -1px<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
