<span class="line">  <span class="token property">grid-template-columns</span><span class="token punctuation">:</span> <span class="token function">min</span><span class="token punctuation">(</span>100%<span class="token punctuation">,</span> <span class="token function">var</span><span class="token punctuation">(</span>--grid-w<span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
